sys_script_client.script:
function onLoad() {

	var value1 = g_form.getValue('sys_select.core_company.vendor_tier');
	switch(value1) {
		case "d3468593677303003b63624ad585ef07": g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'red'; break;
		case "a6b7cd93677303003b63624ad585ef37": g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'tomato'; break;
		case "19d7cd93677303003b63624ad585efd9": g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'orange'; break;
		case "eae701d3677303003b63624ad585efca": g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'yellow'; break;
		case "75f781d3677303003b63624ad585ef84": g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'green'; break;
		default: g_form.getControl('sys_select.core_company.vendor_tier').style.background = 'white'; break;
   }
}


